// 给出一个数组，列出从1到这个数字所有可能的排列组合数组


const perm=xs=>{
    let ret=[];
    for(let i=0;i<xs.length;i=i+1){

        let rest=perm(xs.slice(0,i) + xs.slice(i+1)); // 截取原来的数组，如i是0,则会截取从1开始的字符串。
        if(!rest.length){
            ret.push(xs[i].toString()) // 
        }else{
            for(let j=0;j<rest.length;j=j+1){
                ret.push(xs[i] + rest[j])
            }
        }
    }
    return ret
};

