var findAnagrams = function(s, p) {
    const ls=s.length,lp=p.length;
    const cp = {}; 
    const cs = {};
    const result = [];
    const set = [...new Set(p.split(''))];
    for(let i=0;i< set.length;i++) {
        cp[set[i]] = 0;
    }
    for(let i=0;i<p.length;i++) {
        cp[p[i]] += 1;
    }
    for(let i=0;i<ls;i++) {
        if(!cs[s[i]]) {
            cs[s[i]] = 0;
        }
        cs[s[i]] +=1;
        if(i>=lp) {
            cs[s[i-lp]] -=1;
            if((cs[s[i-lp]])==0) {
                delete cs[s[i-lp]]
            }
        }
        // javascript equal??
        if(sameCounter(cs,cp)) {
            console.log(i,lp);
            result.push(i-lp+1);
            // because of javascript equal ,so not migrate from python to js
        }
    }

    return result;
    
    function sameCounter(a,b) {
        if(Object.keys(a).length !== Object.keys(b).length) {
            return false;
        }
        for(let i in a){
            if(a[i] != b[i]) {
                return false;
            }
        }
        return true;
    }
    
}

